class Solution {

    std::vector<std::vector<int>> dirs = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

    void MarkZeros(vector<vector<int>>& grid, int yy, int xx) {
        grid[yy][xx] = 0;
        int rows = grid.size();
        int cols = grid[0].size();
        for(auto &dir:dirs) {
            int y = yy + dir[0];
            int x = xx + dir[1];
            if(y>=0&&y<rows&&x>=0&&x<cols&&grid[y][x]==1) {
                MarkZeros(grid, y, x);
            }
        }
    }

public:
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int rows = grid1.size();
        int cols = grid1[0].size();
        for(int y=0;y<rows;y++) {
            for(int x=0;x<cols;x++) {
                if(grid1[y][x]==0&&grid2[y][x]==1) {
                    MarkZeros(grid2, y, x);
                }
            }
        }
        int count = 0;
        for(int y=0;y<rows;y++) {
            for(int x=0;x<cols;x++) {
                if(grid2[y][x]==1) {
                    count++;
                    MarkZeros(grid2, y, x);
                }
            }
        }
        return count;
    }
};
