class Solution {
bool isValid(int r, int c, vector<vector<int>>& matrix) {
	return ((r >= 0 && r < (int)matrix.size()) && (c >= 0 && c < (int)matrix[0].size()));
}

void dfs_connection(vector<vector<int>>& grid2,vector<vector<int>>&grid1, vector<vector<bool>>& visited , int i , int j , bool& check) {
	if (!isValid(i, j, grid2) || visited[i][j] || grid2[i][j] != 1)
		return;

	if (grid2[i][j] == 1 && grid2[i][j] != grid1[i][j])
		check = true;

	visited[i][j] = true;

	int dr[] = { -1 , 0, 1, 0 };
	int dc[] = { 0 , 1 , 0, -1 };
	for (auto d = 0; d < 4; ++d)
		dfs_connection(grid2,grid1, visited, i + dr[d], j + dc[d] , check);
}
public:
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        vector<vector<bool>>visited(grid2.size(), vector<bool>(grid2[0].size()));

        int cnt = 0;
        bool check = false;

        for (auto i = 0; i < (int)grid2.size(); ++i)
            for (auto j = 0; j < (int)grid2[i].size(); ++j) {

                if (!visited[i][j] && grid2[i][j]) {
                    dfs_connection(grid2, grid1, visited, i, j , check);

                    check ? check = false : ++cnt;
                }
            }

        return cnt;
    }
};

class Solution {
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        int count = 0;
        for (int i = 0; i < grid1.length; i++) {
            for (int j = 0; j < grid1[0].length; j++) {
                if (grid2[i][j] == 1) {
                    if (count(i, j, grid1, grid2)) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    public boolean count(int i, int j, int[][] grid1, int[][] grid2) {
        if (i < 0 || i >= grid1.length || j < 0 || j >= grid1[0].length || grid2[i][j] == 0) {
            return true;
        }
        if (grid1[i][j] == 0) {
            return false;
        }
        grid2[i][j] = 0;
        return count(i + 1, j, grid1, grid2) &&
               count(i - 1, j, grid1, grid2) &&
               count(i, j + 1, grid1, grid2) &&
               count(i, j - 1, grid1, grid2);
    }
}

