    dummy := &ListNode{Val: 0, Next: head}
    prev := dummy

    for i := 0; i < left-1; i++ {
        prev = prev.Next
    }

    stack := []*ListNode{}
    current := prev.Next

    for i := 0; i < right-left+1; i++ {
        stack = append(stack, current)
        current = current.Next
    }

    for len(stack) > 0 {
        last := len(stack) - 1
        prev.Next = stack[last]
        stack = stack[:last]
        prev = prev.Next
    }

    prev.Next = current

    return dummy.Next
}

