class Solution {
public:
    ListNode*rev(ListNode*head){
        if(head==NULL || head->next==NULL)
            return head;
        ListNode*curr=head;
        ListNode*prev=NULL;
        ListNode*nex=NULL;
        while(curr!=NULL){
            nex=curr->next;
            curr->next=prev;
            prev=curr;
            curr=nex;
        }
        return prev;
    }
    ListNode* reverseBetween(ListNode* head, int left, int right) {
        if(head==NULL || head->next==NULL)
            return head;
        if(left==right)
