                v=prev;
                t=curr;
            }
            if(s1==right){
                s=curr->next;
                curr->next=NULL;
                break;
            }
            prev=curr;
            curr=curr->next;
            s1++;
        }
        if(f==0){
        v->next=NULL;
        }
        ListNode*ans=rev(t);
        t->next=s;
        if(f==0)
        v->next=ans;
        if(f==1)
            head=ans;
        return head;
    }
};
