class Solution {
    func reverseBetween(_ head: ListNode?, _ m: Int, _ n: Int) -> ListNode? {
	  guard head != nil else { return nil }

      let dm: ListNode? = ListNode(0)
      dm?.next = head

      var before = dm

      for _ in 0..<m-1 {
        before = before?.next
      }

      let endOfReverse = before?.next
      var prev = before?.next
      var curr = prev?.next

      for _ in 0..<n-m {
        let next = curr?.next
        curr?.next = prev
        prev = curr
        curr = next
      }

      before?.next = prev
      endOfReverse?.next = curr

      return dm?.next
    }
}
