            next = curr.next;
            curr.next = prev;
            prev = curr;
            curr = next;
        }    
        if(Pleft!=null )  Pleft.next = Tright;
        Tleft.next = Pright;
        if(head.next==null || check)  return Tright;
        return head;
    }
}
