```
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* reverseBetween(ListNode* head, int m, int n) {
        if (!head || m == n)
            return head;

        ListNode dummy(0, head);
        ListNode* front = &dummy;
        ListNode* end = &dummy;

        for (int i = 0; i < m - 1; ++i)
            front = front->next;

        for (int i = 0; i <= n; ++i)
            end = end->next;

        ListNode* inter = front->next;
        stack<ListNode*> stack;

        while (inter != end) {
            stack.push(inter);

            inter = inter->next;
        }

        while (!stack.empty()) {
            front->next = stack.top();
            stack.pop();
            front = front->next;
        }

        front->next = end;

        return dummy.next;
    }
};
```

