class Solution {
    func reverseBetween(_ head: ListNode?, _ m: Int, _ n: Int) -> ListNode? {

        var sentinel = ListNode(0)
        sentinel.next = head
        var prev: ListNode? = sentinel
        var node: ListNode? = head

        var index = 1
        while index < m && node != nil {
            prev = node
            node = node!.next
            index += 1
        }

        var m_tail: ListNode! = prev
        var n_head: ListNode? = node

        var next: ListNode?
        while index <= n && node != nil {
            next = node!.next
            node!.next = prev
            prev = node
            node = next
            index += 1
        }

        m_tail.next = prev
        n_head?.next = node

        return sentinel.next
    }
}
