class Solution {
    public ListNode reverseBetween(ListNode head, int left, int right) {
        if(head==null){
            return null;
        }
        ListNode prev=null;
        ListNode current=head;
        while(left>1){
            prev=current;
            current=current.next;
            left--;
            right--;
        }
        ListNode connection=prev;
        ListNode tail=current;
        while(right>0){
            ListNode temp=current.next;
            current.next=prev;
            prev=current;
            current=temp;
            right--;
        }
        if(connection!=null){
            connection.next=prev;
        }else{
            head=prev;
        }
        tail.next=current;
        return head;
    }
}

class Solution {
public:
    ListNode* reverseBetween(ListNode* head, int left, int right) {
        ListNode* l1=head;
        // int arr[right-left+1];

        ListNode* l2=head;
        if(l1->next==NULL  && l2->next==NULL)
            return head;
        int r=right,l=left-1;
        vector<int>v;
        while(l1)
        {
            int x= l1->val;
            v.push_back(x);
            l1=l1->next;
        }
        reverse(v.begin()+l,v.begin()+r);
        int i=0;
        while(l2)
        {
            l2->val=v[i];
            l2=l2->next;
            i++;
        }
        return head;
    }
};

