class Solution {
public:
    vector<string> restoreIpAddresses(string str) {

        vector<string>v;
        string ans;
        helper(ans, str, 0, 0,v);
        return v;

    }

    void helper(string ans, string str, int idx, int k,vector<string>&v){

        if(k==4 ){
            if(idx>=str.length()){
                ans.pop_back();
                v.push_back(ans);
            }
            return ;
        }

        string ip;
        for(int i=idx;i<str.length();i++){
            if(ip=="0"){
                return ;
            }
             ip=ip+str[i];

            if(stoi(ip)>=0 && stoi(ip) <=255){
                string x=ans;
                ans=ans+ip+".";

                helper(ans,str,i+1,k+1,v);

                ans=x;
            }

            else if(stoi(ip)>255){
                return;
            }
        }
    }

};


