This is classical backtracking challenge.
I listed few more solutions for simmilar problems below:
* https://leetcode.com/problems/restore-ip-addresses
```
/**
 * @param {string} s
 * @return {string[]}
 */
var restoreIpAddresses = function(s) {
    const result = [];
    backtrack([], s, 0);
    return result;

    function backtrack(tempArr, s, start) {
        if (start === s.length && tempArr.length === 4) {
            result.push(tempArr.join('.'));
            return;
        } 

        if (tempArr.length === 4 || start > s.length) {
            return;
        }

        for (let i = 1; i < 4; i++) {
            const sub = s.substring(start, start + i);
            if (sub.length > 1 && sub[0] === '0') {
                continue;
            }
            const int = parseInt(sub);
            if (int < 256 && int >= 0) {
                // 1:
                // tempArr.push(sub);
                // backtrack(tempArr, s, start + i);
                // tempArr.pop();
                // 2:
                backtrack([...tempArr, sub], s, start + i);
            }
        }
    }
};
```
* https://leetcode.com/problems/combination-sum
```
/**
 * @param {number[]} candidates
 * @param {number} target
 * @return {number[][]}
 */
var combinationSum = function(candidates, target) {
    let result = [];
    backtrack([], target, 0);
    return result;

    function backtrack(tempArr, remain, start){
        if(remain < 0){
            return;
        }
        else if (remain === 0){
            result.push(tempArr);
        }
        else {
            for(let i = start; i < candidates.length; i++){
                // 1:
                // tempArr.push(candidates[i]);
                // backtrack([...tempArr], remain - candidates[i], i);
                // tempArr.pop();
                // 2:
                backtrack([...tempArr, candidates[i]], remain - candidates[i], i); // not i+1, because we can reuse same elements
            }
        }
    }
};
