class Solution {
    public List<String> restoreIpAddresses(String s) {
        List<String> ans = new LinkedList<>();
        // Call the helper method with the input string, the ans list, a counter initialized to 0, and empty strings for the current decimal and current IP address
        helper(s,ans,0,"","");
        return ans;
    }

    public void helper(String s, List<String> ans, int dec, String r ,String a){
        if(r.length()>2 && Integer.parseInt(r)>255)return;
        // If the number of parts in the current IP address is greater than 4, return
        if(dec>4) return;
        // If the current decimal String has more than 1 digit and starts with 0, return
        if(r.length()>1 && Integer.parseInt(r.charAt(0)+"")==0)return;
        // Concatenate the current decimal to the current IP address
        a+=r;
        // If the number of decimals is 4 and the input string is empty, add the current IP address to the list of valid IP addresses
        if(dec==4 && s.length()==0)ans.add(a);
        // If the current decimal is not empty, add a period to the current IP address
        if(r.length()>0)a+=".";
        // Recursively call the helper method with various substrings of the input string, incremented counter and the current IP address
        if(s.length()>0) helper(s.substring(1,s.length()),ans,dec+1,s.substring(0,1),a);
        if(s.length()>1) helper(s.substring(2,s.length()),ans,dec+1,s.substring(0,2),a);
        if(s.length()>2) helper(s.substring(3,s.length()),ans,dec+1,s.substring(0,3),a);
    }
}

