public void backtrack(List<String> res, String s, String curr, int start, int level) {
	if(level >= 4) {
		if(start == s.length()) {
			res.add(curr);
		}
		return;
	}
	for(int i = start; i < Math.min(start+3, s.length()); i++) {
		if(start != i && s.charAt(start) == '0') continue;
		int num = Integer.parseInt(s.substring(start, i+1));
		if(num > 255) break;
		if(curr == "") {
			backtrack(res, s, ""+ num, i+1, level+1);
		} else {
			backtrack(res, s, curr + "."+ num, i+1, level+1);
		}
	}
}

