-> We simply have to divide the given string into 4 parts where integer value of each part should be between 0 to 255 .


```
class Solution {
public:
    vector<vector<string>> local ;
    int n ;

	// function to calculate the integer value of a string
    int stoint(string s){
        int num = 0 ;
        for(auto c : s){
            num = num*10 + (c-'0');
        }
        return num ;
    }

    void helper(string s , int idx , vector<string>& path , int part){
		if(idx==n && part==0){         // we have reached the end of the string and also divided it in 4 parts
            local.push_back(path) ;     // so we can include this path in our answer.
            return ; 
        }

        for(int i=idx ;  i<n ; i++){
            if(i-idx+1 > 3)             // each part can't be greater than 255 so not more than 3 digits
                return ;
            string temp = s.substr(idx , i-idx+1) ;
            if(stoint(temp)>=0 && stoint(temp)<=255){
                if(temp[0]=='0' && temp.length()>1) return ;   // As we can't have strings with leading zeroes
                path.push_back(temp) ;
                helper(s , i+1 , path , part-1) ;    
                path.pop_back() ;      // simple backtracking 
            }
        } 
    }

    vector<string> restoreIpAddresses(string s) {
        n =  s.length() ;
        vector<string> path ;
       if(n<4 || n>12)   return {} ;

        helper(s , 0 , path , 4) ;
        vector<string> ans  ; 
        for(auto arr : local){   // creating our final ans array
            string temp  = "";
            for(auto t : arr ){
                temp += t ;
                temp += '.' ;
            }
            temp.pop_back() ;   //as last element of string will be '.' 
            ans.push_back(temp) ;
        }
        return ans  ;
    }
};
// Plz upvote if it helped.
