class Solution {
    List<String> ans = new ArrayList<>();
    public List<String> restoreIpAddresses(String s) {
        if (s.length()>12) return new ArrayList<>();
        computeIP("", s, 0, 0);
        return ans;
    }
    public void computeIP(String cur, String s, int idx, int seg_num) {
        if (seg_num==4 && idx>=s.length()) ans.add(cur);
        for (int i = 1; i <= Math.min(3, s.length()-idx); i++) {
            String sub = s.substring(idx, idx+i);
            if (sub.length()>1&&sub.charAt(0)=='0') return;
            int temp = Integer.parseInt(sub);
            if (temp<=255 && temp>=0) {
                if (!cur.equals("")) {
                    computeIP(cur.concat("." + sub), s, idx+i, seg_num+1);
                }
                else {
                    computeIP(cur.concat(sub), s, idx+i, seg_num+1);
                }
            }
        }
    }
}
