class Solution {
public:
    int n;
    vector<string> result;
    // check validity of the string i.e. str should not start with 0 and should be in range of 0-255
    bool isValid(string str) {
        if(str[0] == '0')
            return false;

        //convert string to integer
        int val = stoi(str);

        return val <= 255;
    }
    // recursive function for generating ip address
    void solve(string& s, int idx, int part, string curr) {
        //valid ip is only possible when all the char of string is consumed and it has only 4 parts
        if(idx == n && part == 4) {
            result.push_back(curr.substr(0, curr.length()-1));
            return;
        }
        // if we consider 1 substr then the index range must be valid
        if(idx+1 <= n)
            solve(s, idx+1, part+1, curr + s.substr(idx, 1) + ".");
        // if we choose 2 or 3 substr then the index and the str must have validity check
        if(idx+2 <= n && isValid(s.substr(idx, 2))) {
            solve(s, idx+2, part+1, curr + s.substr(idx, 2) + ".");
        }

        if(idx+3 <= n && isValid(s.substr(idx, 3))) {
            solve(s, idx+3, part+1, curr + s.substr(idx, 3) + ".");
        }
    }

    vector<string> restoreIpAddresses(string s) {
        result.clear();

        n = s.length();
        // we cannot divide into 4 parts in which length is greater than 12
        if(n > 12)
          return result;

        int part = 0;
        string curr = "";

        solve(s, 0, part, curr);
        return result;

    }
};
