class Solution {

    public List<String> restoreIpAddresses(String s) {
        if (s == null || s.length() < 4 || s.length() > 12) {
            // return empty list if input is null or not in the correct length range
            return new ArrayList<>();
        }

        List<String> ans = new ArrayList<>();
        StringBuilder sb = new StringBuilder(s);
        dfs(sb, 0, 3, ans);

        return ans;
    }


    private void dfs(StringBuilder sb, int index, int dotCount, List<String> ans) {
        if (dotCount == 0) {
            // base case, if all dots have been added, check if the IP address is valid
            if (isValid(sb)) {
                ans.add(sb.toString());
            }
            return;
        }

        if (index >= sb.length()) {
            return;
        }

        //backtracking

        sb.insert(index, ".");
        dfs(sb, index + 2, dotCount - 1, ans);
        sb.deleteCharAt(index);

        dfs(sb, index + 1, dotCount, ans);
    }


    private boolean isValid(StringBuilder sb) {
        String[] parts = sb.toString().split("\\.");
        if (parts.length != 4) {
            return false;
        }

        for (String part : parts) {
            if (part.length() == 0 || part.length() > 3) {
                return false;
            }

            if (part.startsWith("0") && part.length() > 1) {
                return false;
            }

            int num = Integer.parseInt(part);
            if (num > 255) {
                return false;
            }
        }

        return true;
    }
}

