class Solution {
    public List<String> restoreIpAddresses(String s) {
        //list to return and store ans
        List<String> ans = new ArrayList<>();

        //calling helper function
        //params(provided_string , ans_generated , dot_count , answer)
        solve(s , "" , 4, ans);
        return ans;
    }

    void solve(String str , String curr, int dotCount, List<String> ans){
        if(dotCount == 0 && str.length() == 0)
        {
            //add answer except the last '.' if we reach to end of string and dots are also exhausted 
            ans.add(curr.substring(0, curr.length() - 1));
            return;
        }

        //min_length as we can only take max of 3 nums in ip address
        //we check if we have the length of string more than 3 length so just skip the other char of string
        int minLength = Math.min(str.length() , 3);

        for(int i = 0; i < minLength; i++){
            String s = str.substring(0,i+1); // add the char to our ans one by one
            if(check(s)){ // check if we have the correct number within range
                solve(str.substring(i + 1) , curr+s+'.' , dotCount-1 , ans); // call recursive function by decresing dots count
            }
        }
    }

    boolean check(String str){
        //check for zero eg - '01' is not valid only '0.1.3' is accepted
        if(str.length() > 1 && str.charAt(0) == '0')
            return false;

        //check for range of number (0 - 255)
        if(Integer.parseInt(str) > 255)
            return false;

        //reached here indicates our string can be a valid answer
        return true; 
    }
}
