class Solution {
public:
    vector<string> ans;

    void restore(string &s,int n,int i,int dots,string t)
    {
        if(i==n)
        {
            if(dots==4)
            {
                t.pop_back();
                ans.push_back(t);
            }

            return;
        }

        if(dots>3)
        return;

        if(s[i]=='0')
        {
            restore(s,n,i+1,dots+1,t+s[i]+".");
            return;
