        string x="";
        for(int j=0;j<3 && i+j<n;j++)
        {
            x+=s[i+j];

            if(stoi(x)>255)
            return;

            restore(s,n,i+j+1,dots+1,t+x+".");
        }
    }

    vector<string> restoreIpAddresses(string s) {
        restore(s,s.length(),0,0,"");
        return ans;
    }
};
