class Solution {

    bool isValid(const string& part){
        if((part[0] == '0' && part.size() != 1) || part.size() > 3 || stoi(part) > 255)
            return false;
        return true;
    }

    void util(const string& str, int s, string formedString, string subres, vector<string>& result, int count){
        if(formedString!= "" && (!isValid(formedString) || count > 4)){
            return;
        }
        subres += formedString == "" ? "" : formedString + ".";
        if(subres.size()-4 == str.size()){
            subres.pop_back();
            result.push_back(subres);
            return;
        }
        formedString = "";
        for(int i = s; i < s+3 && i < str.size(); i++){
            formedString.append(1, str[i]);
            util(str, i+1, formedString, subres, result, count+1);
        }
    }

public:
    vector<string> restoreIpAddresses(string s) {
        vector<string> result;
        util(s, 0, "", "", result, 0);
        return result;
    }
};
