class Solution {
public:
    void bt(string s,string x,int i,int cnt,vector<string> &ans)
    {
		if(cnt>4)
        {
            return;
        }
        if(i==s.size() && cnt==4)
        {
            x=x.substr(0,x.size()-1);
            ans.push_back(x);
            return;
        }
        int p=0;
        for(int j=i;j<s.size();j++)
        {
            if(j!=i && p==0) //to check of recuring zeros
            {
                break;
            }
            p*=10;
            p+=s[j]-'0';
            if(p>=0 && p<=255)
            {
                string y=x;
                x+=to_string(p);
                x+='.';
                bt(s,x,j+1,cnt+1,ans);
                x=y;
            }
            if(p>255)
            {
                break;
            }
        }
    }
    vector<string> restoreIpAddresses(string s) {
       vector<string> ans;
        if(s.length()>12) //length must be less than 12
        {
            return ans;
        }
        string x;
        bt(s,x,0,0,ans);
        return ans;
    }
};
