// the backtrack is if the sliced ip field is valid then go to next recursive

var restoreIpAddresses = function(s) {
  const res = [];
  if(s.length < 4) return res;
  dfs([], s, res);
  return res;
};


function dfs(tmp, s, res) {
  if(tmp.length === 4) {
    if(s === '') res.push(tmp.join('.'));
    return;
  }

  for(let i = 1; i <= 3; i++) {
    if(s.length >= i) {
      const ipItem = s.slice(0, i);
      const ipItemVal = parseInt(ipItem);
      if(ipItemVal >= 0 && ipItemVal <= 255 && ipItem === ipItemVal.toString()) {
        dfs([...tmp, ipItem], s.slice(i), res);
      }
    }
  }
}
