{
    int p=0;int j=0;
    while(j<temp.size())
    {
        if(temp[j+1]=='.')
        {
            string eval;
            while(p<=j)
            {
                eval.push_back(temp[p]);p++;

            }
            int evall=stoi(eval);
                if(eval[0]=='0' && eval.size()>1){return false;}
                if(evall>255){return false;}
            p=j+2;
            j=p;
            continue;
        }
        j++;
    }

