			if (tokens[0].charAt(0) == '(' && tokens[0].charAt(4) == ')'  && onlyDigits(tokens[0].substring(1 , 4)) && onlyDigits(tokens[0].substring(6)) && onlyDigits(tokens[1])) {
				return true;
			}
		}

		for (int i = 0 ; i < tokens.length ; i++) {
			System.out.println(tokens[i]);
		}
		return false;
