// Dijkstra Solution
// Note 1: Check whether node.stop > k
// Note 2: Add to heap 1) distance is shorter; 2) number of stops are less.
// Time complexity: O(N + ElogN)
// Space complexity: O(N)
class Solution {
    public int findCheapestPrice(int n, int[][] flights, int src, int dst, int k) {
        List<Node>[] graph = new ArrayList[n];
        buildGraph(graph, flights);
        int[] prices = new int[n];
        int[] stops = new int[n];
        Arrays.fill(prices, Integer.MAX_VALUE);
        Arrays.fill(stops, Integer.MAX_VALUE);
        prices[src] = 0;
        stops[src] = 0;
        PriorityQueue<Node> heap = new PriorityQueue<>((n1, n2) -> Integer.compare(n1.price, n2.price));
        heap.add(new Node(src, 0, 0));  // id, price, stop
        while (!heap.isEmpty()) {
            Node node = heap.poll();
            if (node.id == dst) return node.price;
            if (node.stop > k) continue;
            for (Node neighbor : graph[node.id]) {
                int nextCost = node.price + neighbor.price;
                int nextStop = node.stop+1;
                if (prices[neighbor.id] > nextCost) {
                    // if nextCost is less than price, update prices and stops array, and explore the neighbor.
                    prices[neighbor.id] = nextCost;
                    stops[neighbor.id] = nextStop;
                    heap.add(new Node(neighbor.id, nextCost, nextStop));
                } else if (stops[neighbor.id] > node.stop) {
                    // if node.stop < stops[neighbor.label], explore the neighbor.
                    heap.add(new Node(neighbor.id, nextCost, nextStop));
                }
            }
        }
        return prices[dst] == Integer.MAX_VALUE ? -1 : prices[dst];
    }

    private void buildGraph(List<Node>[] graph, int[][] flights) {
        for (int i = 0; i < graph.length; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int[] flight : flights) {
            int from = flight[0], to = flight[1], price = flight[2];
            graph[from].add(new Node(to, price, 0));
        }
    }

    static class Node {
        int id, price, stop;
        Node(int id, int price, int stop) {
            this.id = id;
            this.price = price;
            this.stop = stop;
        }
    }
}
