        queue <pair<int , pair<int, int>>> q;
        q.push({0, {0, src}});
        vector <int> cost(n, 1e9);
        cost[src] = 0;
        while (!q.empty()) {
            int stops = q.front().first;
            int ct = q.front().second.first;
            int node = q.front().second.second;
            q.pop();    
            if (stops > k)
                continue;
            for (auto it: adj[node]) {
                int adjNode = it.first;
                int wt = it.second;
                if (ct + wt < cost[adjNode]) {
                    cost[adjNode] = ct + wt;
                    q.push({stops+1, {ct + wt, adjNode}});
                }
            }
        }
        if (cost[dst] == 1e9)
            return -1;
        return cost[dst];    
    }
};
