let findCheapestPrice = (N, E, S, T, K) => {
    let pre = Array(N).fill(Infinity); pre[S] = 0;
    while (0 <= K--) {
        let cur = [...pre];
        for (let [u, v, w] of E)
            cur[v] = Math.min(cur[v], pre[u] + w);
        pre = cur;
    }
    return pre[T] != Infinity ? pre[T] : -1;
};
```

