        for(int i = 0; i < flights.size(); i++) {
            adj[flights[i][0]][flights[i][1]] = flights[i][2];
        }

        vector<vector<int>> dp(k+2, vector<int> (n, -1));
        int val = f(k+1, src, dst, n, adj, dp);
        return (val == 1e7 ? -1 : val);
    }
};
