class Solution:
    def findCheapestPrice(self, n: int, flights: List[List[int]], src: int, dst: int, k: int) -> int:
        NO_ROUTE = -1

        fs = dict() ## convert flights to dictionary form
        for fr,to,price in flights:
            if fr in fs:
                fs[fr].append([to, price])
            else:
                fs[fr] = [[to, price]]

        if src not in fs: return NO_ROUTE ## edge case

        cheapest, l = None, fs[src]
        for i in range(k+1):
            if not l: break
            l_new = []
            for t1,p1 in l:
                if t1==dst: 
                    cheapest = min(cheapest,p1) if cheapest else p1
                elif i<=k and t1 in fs: 
                    l_new += [[t2,p1+p2] for t2,p2 in fs[t1]]
            l = l_new.copy()
        return cheapest if cheapest else NO_ROUTE
