def findCheapestPrice(self, n: int, flights: List[List[int]], src: int, dst: int, K: int) -> int:
    if not flights or not flights[0]:
        return -1
    res = [float('inf')] * n
    grid = collections.defaultdict(list)
    for s, d, c in flights:
        grid[s].append([d, c])

    heap = [(0,src,0)] 
