namespace {

constexpr int T(const int n) {
    if (n == 0) return 0;
    if (n <= 2) return 1;

    int a = 0;
    int b = 1;
    int c = 1;
    for (int i = 3; i <= n; ++i) {
        const int next = a + b + c;
        a = b;
        b = c;
        c = next;
    }
    return c;
}

constexpr auto generate() {
    array<int, 38> ans = {};
    for (int i = 0; i < ans.size(); ++i) {
        ans[i] = T(i);
    }
    return ans;
}

constexpr auto Ts = generate();

}  // namespace

class Solution {
public:
    static constexpr int tribonacci(int n) {
        return Ts[n];
    }
};
