public int visiblePoints(List<List<Integer>> points, int angle, List<Integer> location) {
    List<Double> ang = new ArrayList<>();
    int selfe = 0;
    for (int i = 0; i < points.size(); i++) {
        List<Integer> point = points.get(i);
        int x = point.get(0);
        int y = point.get(1);
        if (x == location.get(0) && y == location.get(1)) {
            selfe++;
            continue;
        }
        float tang = (y - location.get(1)) * 1.0f / ((x - location.get(0)) * 1.0f);
        double an = Math.atan(tang);
        double deg = an / Math.PI * 180;
        if (x < location.get(0)) deg += 180;
        ang.add(deg);
    }
    Collections.sort(ang);
    int max = 0;
    Deque<Double> dq = new ArrayDeque<>();
    for (int i = 0; i < ang.size(); i++) {
        dq.addLast(ang.get(i));
        while (dq.peekLast() - dq.peekFirst() > angle) dq.pollFirst();
        max = Math.max(dq.size(),max);
    }
    for (int i = 0; i < ang.size(); i++) {
        dq.addLast(ang.get(i) + 360);
        while (dq.peekLast() - dq.peekFirst() > angle) dq.pollFirst();
        max = Math.max(dq.size(),max);
    }
    return  max + selfe;
}
