```
    public int visiblePoints(List<List<Integer>> p, int d, List<Integer> lo) { 
        int n = p.size();
        int x = lo.get(0);
        int y = lo.get(1);

		// Some points are the same with the original points, process these points first
        int cnt0 = 0;
        for(List<Integer> l: p){
            int x0 = l.get(0);
            int y0= l.get(1);

            if(x == x0 && y == y0) cnt0++; 
        }

		// make an array to store angles 
        double [] A = new double[n - cnt0];

        int idx = 0;
        for(List<Integer> l : p){
            int x0 = l.get(0);
            int y0= l.get(1);
            if(x == x0 && y == y0) continue;;
            double de = Math.atan2(y0 - y, x0 - x);
            A[idx] = 180*de/Math.PI;
            idx++;
        }


        Arrays.sort(A);
        int s = 0;
        int max = 0;
        int cnt = 0;
        int z = n - cnt0; //

		//Sliding window
		// Notice the 360 degree problem,
        for(int i = 0; i < 2*(n - cnt0); i++){
            double dd = 0.0;
            if(i < z){
                dd = A[i];
            }
            if(i >= z){
                dd = A[i%z] + 360; // process 360 degree problem
            }
            while(s < z && dd - A[s] > d){
                s++;
            }
            if(s >= z) break;
            cnt = i - s + 1;
            if(cnt > max){
                max = cnt;
            } 
        }

        return max + cnt0;

    }

