class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        // calc and sort angles and keep track of num of orgs (same point as location)
        vector<float> angles;
        int orgs = 0;
        for(auto p: points) {
            int dx = p[0] - location[0];
            int dy = p[1] - location[1];
            if(dx==0 && dy==0)
                orgs++;
            else
                angles.push_back(atan2(dy, dx) * 180. / M_PI);
        }
        sort(angles.begin(), angles.end());

        // count in sweeping window (handle overflow with modulo)
        int mx = 0;
        int j = 0;
        int n = angles.size();
        for(int i = 0; i < n; ++i) {
            while(angles[j % n] + 360 * (j / n) - angles[i] <= angle)
                ++j;
            mx = max(mx, j-i);
        }

        return mx + orgs;
    }
};
