        int left=0, res=0, m=v.size();
        sort(v.begin(), v.end());
        for(int i=0; i<m; i++) v.push_back(360.0+v[i]);
        for(int right=0; right<v.size(); right++){
            while(v[right]-v[left]>(double)angle) left++;
            res=max(res, right-left+1);
        }
        return res+ans;
    }
};
