```java

// static imports are bad; I've only kept them for readability
import static java.lang.Math.*;

public class Solution {

    private double cartesianToAngle(List<Integer> point, List<Integer> location) {
        return atan2(point.get(1) - location.get(1), point.get(0) - location.get(1));
    }

    public int visiblePoints(List<List<Integer>> points, int angle, List<Integer> location) {

        int numberOfPointsOnLocation = 0;
        List<Double> pointsNotOnLocationAsAngles = new ArrayList<>();
        for (List<Integer> point : points) {
            if (location.get(1).equals(point.get(1)) && location.get(0).equals(point.get(0))) {
                numberOfPointsOnLocation++;
            } else {
                pointsNotOnLocationAsAngles.add(cartesianToAngle(point, location));
            }
        }

        Collections.sort(pointsNotOnLocationAsAngles);
        List<Double> transformedAngles = new ArrayList<>(pointsNotOnLocationAsAngles);

        // when two points -179 and 179 are only 2 degrees apart but after sorting, they are at extreme ends.
        // To avoid using a bunch of if/else conditions to detect such cases, for simplicity,
        // we just add 2 * PI to all points and process them again. This will bring points like -179 and 179 together
        // by transforming, say -179 to 181 and we're happy.
        for (Double theta : pointsNotOnLocationAsAngles) {
            transformedAngles.add(theta + 2 * PI);
        }
        int maxPointsInWindow = 0;
        Double angleInRadian = angle * PI / 180;

        int left = 0;
        for (int right = 0; right < transformedAngles.size(); right++) {
            while (transformedAngles.get(right) - transformedAngles.get(left) > angleInRadian) {
                left++;
            }
            maxPointsInWindow = max(maxPointsInWindow, right - left + 1);
        }
        return maxPointsInWindow + numberOfPointsOnLocation;
    }
}
