```
class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        vector<double> thetas;
        int numPtInLocation = 0;

        for (vector<int>& point : points) {
            int dx = point[0] - location[0], dy = point[1] - location[1];

            if (dx == 0 && dy ==0) {
                ++numPtInLocation;
                continue;
            }

            // Take the location of the person as the new origin of the coordinate
            // and calculate the theta of each point.
            thetas.push_back(atan2(dx, dy));

            // Because the person is going to rotate 360 degree to decide the maximum
            // number of points that he/she can see, the left and right limit of his/her
            // visible range will beyond 360. 
            thetas.push_back(thetas.back() + 2 * M_PI);
        }

        // Sort the points by the thetas.
        sort(begin(thetas), end(thetas));

        // Use sliding window algorithm to decide the maximum number of points that
        // the person can see.
        int maxCnt = 0, left = 0, right = 0;
        double angle2 = static_cast<double>(angle) * M_PI / 180;
        while (right < thetas.size()) {
            while (right < thetas.size() && thetas[right] - thetas[left] <= angle2) {
                ++right;
            }
            maxCnt = max(maxCnt, right - left);
            ++left;
        }

        return maxCnt + numPtInLocation;

    }
};
```

