class Solution{
  public:    
    int visiblePoints(vector<vector<int>>& points, int looking_angle, vector<int>& location){
	 // Calculate angle of each point
      auto Get_Angle = [](vector<int>& p1, vector<int>& p2){
        double angle = atan2(p1[1] - p2[1], p1[0] - p2[0]);
        return (angle >= 0 ? angle : 2 * M_PI + angle) * 180.0 / M_PI;
      };
      vector<double> angles;
      int always_visible = 0;
      for(auto& point : points){
        if(point == location){
          always_visible++;
        }else{
          angles.emplace_back(Get_Angle(point, location));
        }
      }
      sort(angles.begin(), angles.end());      
      int res = 0;	  
      for(auto& start_angle : angles){
	    // find all the points between [start_angle, end_angle] (both inclusive)
        double end_angle = start_angle + looking_angle;
        auto lb = lower_bound(angles.begin(), angles.end(), start_angle);
        auto ub = upper_bound(angles.begin(), angles.end(), end_angle);
        int dist = ub - lb;
        if(end_angle >= 360.0){
		  // when end_angle is more than 360.0 we search from the beginning after subtracting 360.0
          dist += (upper_bound(angles.begin(), angles.end(), end_angle - 360.0) - angles.begin());          
        }
        res = max(res, dist);
      }
      return res + always_visible;
    }    
};

