class Solution {
public:
    double PI=3.141592653589793238;
    double findangle(vector<int> l,vector<int> p)
    {     
        int q=0;
        //point x axis is greater than location x axis
        if(p[0]>=l[0])//quadrant 1 or quadrant 4
        {
            if(p[1]>=l[1]) //if point y axis is greater
                q=1;       //then it belong to quadrant 1
            else
                q=4;       //else belong to quadrant 4

        }//point x axis is lesser than location x axis
        else//it belong to quadrant 2 or quadrant 3
        {
            if(p[1]>=l[1]) //if point y axis is greater
                q=2;       //then it belong to quadrant 2
            else
                q=3;       //else belong to quadrant 3
        }    
        double dely=abs(p[1]-l[1]);
        double delx=abs(p[0]-l[0]);
        //This is basic tan-1(dely/delx) for finding angle between them
        double angle=atan(dely/delx);
        angle*=(double)180;
        angle/=PI;
        double r=angle;
        //This is illustated in given daigram
        if(q==4)
            r=360.0-angle;
        if(q==2)
            r=180.0-angle;
        if(q==3)
            r=180.0+angle;

        if(r>=360.0)
          r-=360.0;

        return r;
    }
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        vector<double> deg;
        int common=0;
        int ans=0;
        for(auto v: points)
        {
        if(v==location)common++;  //same point as location
        else
        deg.push_back(findangle(location,v)); //differnt point from location
        }

        //Basic sliding window technique 
        //For finding answer
        sort(deg.begin(),deg.end());
        vector<double> temp=deg;
        for(int i=0;i<temp.size();i++)
            temp[i]+=360.0;

        deg.insert(deg.end(),temp.begin(),temp.end());

        for(int i=0,j=0;i<deg.size();i++)
        {
            while(j<deg.size() && deg[j]-deg[i]<=(double)(angle)+1e-6)
            j++;

            ans=max(ans,j-i);
        }

        return ans+common;
    }
};
