class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        int inplace = 0;
        vector<float> angles;
        for(int i=0; i<points.size(); i++)
        {
            if(points[i][0] == location[0] && points[i][1] == location[1]) 
            {
                inplace++;
            }
            else
            {
                angles.push_back(calculate(points[i][0], points[i][1], location[0], location[1]));               
            }
        }

        int n = angles.size();
        sort(angles.begin(), angles.end());
        angles.insert(angles.end(), angles.begin(), angles.end());

        int ans = 0;
        int l = 0, r = 0, cur = 0;
        while(l<n && r<2*n)
        {
            while(l<n && r<2*n)
            {
                if(diff(angles[r], angles[l]) <= angle)
                {
                    cur++;
                    r++;
                }
                else
                {
                    break;
                }

                if((r%n)==l)
                {
                    break;
                }
            }

            ans = max(ans, cur);
            l++;
            cur--;
        }

        return ans + inplace;
    }

    float diff(float angle1, float angle2)
    {
        return angle1 - angle2 < 0 ? angle1 -angle2 + (float)360.0 : angle1 - angle2;
    }

    float calculate(int x1, int y1, int x2, int y2)
    {
        float angle = atan2(y1 - y2, x1 - x2);
        angle = angle * 180 / M_PI;
        return angle >= 0.0 ? angle : angle + 360;
    }
};
