class Solution {
public:
    #define to_rad 0.0174532925199432957692369
    #define TWOPI 6.28318530717958647692528676655    
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        for (auto &i: points){
            i[0] -= location[0];
            i[1] -= location[1];
        }
        //number of points with undefined angle
        int offset = 0;
        vector<float> angles;
        float view = (angle * to_rad) + 0.0000000001; 
        for (auto i: points){
            if (i[0] == 0 && i[1] == 0){
                ++offset;
            }
            else {
                angles.push_back(i[1] >= 0 ? acos(i[0]/(sqrt(i[0] * i[0] + i[1] * i[1]))) : TWOPI - acos(i[0]/(sqrt(i[0] * i[0] + i[1] * i[1]))));
            }
        }
        int n = angles.size(), N = 2*n;
        sort(angles.begin(),angles.end());
        //easy way of not dealing with circular sliding window, just append the entire vector over itself adding 2 * pi to the second half
        for (int i = 0; i < n; ++i) angles.push_back(angles[i] + TWOPI);

        int output = 0;
        for (int i = 0, j = 0; i < n; ++i){
            while (angles[j] <= angles[i] + view ){
                ++j;
            }
            output = max(output,j - i); 
        }
        return output + offset;
    }
};
