func visiblePoints(points [][]int, angle int, location []int) int {
	if angle == 360 {
		return len(points)
	}

	samePoint := 0

	//Transform points into relative angles
	angs := make([]float64, 0, len(points))
	for i := range points {
		x := float64(points[i][0] - location[0])
		y := float64(points[i][1] - location[1])

		if x == 0 && y == 0 {
			samePoint++
		} else {
			angs = append(angs, math.Atan2(y, x))
		}
	}

	//Sort
	sort.Float64s(angs)

	vision := float64(angle) * math.Pi / 180.0
	maxi := 0
	loop := false
	addPi := 0.0
	for lead, follow := 0, 0; follow < len(angs); {
		if loop {
			addPi = 2 * math.Pi
		}
		if angs[lead]+addPi-angs[follow] <= vision {
			if lead >= follow {
				maxi = max(maxi, lead-follow+1)
			} else {
				maxi = max(maxi, len(angs)-follow+1+lead)
			}

			lead++
		} else {
			follow++
		}

		if lead >= len(angs) {
			lead = 0
			loop = true
		}

	}

	return samePoint + maxi
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

