                incline.push_back(360);
            }
            else{
                incline.push_back(180);
                incline.push_back(540);
            }
        }
        else{
            double ang = atan2(y, x) * (180 / pi);
            if(ang < 0){
                ang += 360;
            }
            incline.push_back(ang);
            incline.push_back(ang + 360);
