class Solution {
    func visiblePoints(_ points: [[Int]], _ angle: Int, _ location: [Int]) -> Int {
        var count = 0

        var angles: [Double] = points.compactMap { 
            if $0 == location {
                count += 1 
                return nil
            }

            return createAngle(fromPoint: $0, origin: location)
        }

        angles = angles.sorted()
        angles += angles.map { $0 + 360 }

        var l = 0
        var maxCount = 0

        for r in 0..<angles.count {
            while angles[r] - angles[l] > Double(angle) { l += 1 }
            maxCount = max(maxCount, r - l + 1)
        }

        return maxCount + count
    }

    func createAngle(fromPoint point: [Int], origin: [Int]) -> Double {
        let x = point[0] - origin[0]
        let y = point[1] - origin[1]

        let res = atan(Double(y) / Double(x)) / Double.pi * 180

        if x < 0 { return res + 180 }
        if y < 0 { return res + 360 }

        return res
    }
}
