```
class Solution
{
public:

    // This code uses radians instead of degrees
    // double is used because float won't work for a few test cases.
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location)
    {
    	// no need to compute
        if (angle == 360)
        {
            return points.size();
        }

        // no need to compute
        if (points.empty())
        {
            return 0;
        }

		// this will give a precise value of PI = 3.14159
        const double pi = acos(-1); 

    	// field of view, converted to radian from degree
        const double fov = angle * pi / 180.0;

    	// array of radian values
        vector<double> v;

    	// variable that stores all points at the location
        int num_zero = 0;

    	// compute radian values
    	for (int i = 0; i < points.size(); i++)
        {
        	// if a point is at the location then atan2(0, 0) = 0 
			// and it can mess up with the sliding window computation
            if(points[i][0] == location[0] && points[i][1] == location[1])
            {	
                num_zero++;
                continue;
            }

            int x = points[i][0] - location[0];
            int y = points[i][1] - location[1];

    		// save radian value to array v
    		// note that in C++ atan2(y, x)
            double rad = atan2(y, x);
            v.push_back(rad);
        }

        // sorting
        // the order is quadrant 4 --> quadrant 3 --> quadrant 1 --> quadrant 2
    	// elements in v are in range [-PI, PI]
        sort(v.begin(), v.end());

    	// looking for the first occurrence of a point in quadrant 1 or 2 (0 to PI)
        int i_positive = 0;
        for (int i = 0; i < v.size(); i++)
        {
            if (v[i] >= 0)
            {
                i_positive = i;
                break;
            }
        }

        // Points in quadrant 3 and quadrant 4 are then appended to v
        if (i_positive > 0)
        {
            double pi_2 = pi * 2; // PI*2
            int ori_sz = v.size();
            v.insert(v.end(),  // add to the end of array v
					    v.begin(), // first index of duplicated point
					    v.begin() + i_positive); // last index of duplicated point

            // To deal with a cycle,
            // we need to add PI*2 to the duplicated values
            // because the fov can intersect both quadrant 2 and quadrant 3
        	//
            // elements in v are now in range [-PI, PI*2]
            for (int i = ori_sz; i < v.size(); i++)
            {
                v[i] += pi_2;
            }
        }

        // max points inside fov
        int max_pt = 0;

    	// sliding window using two pointers left and right
    	int left = 0;
        for(int right = 0; right < v.size(); right++)
    	{
    		while((v[right] - v[left]) > fov)
    		{
                left++;
    		}
            max_pt = max(max_pt, right - left + 1);
    	}

    	// result
        return max_pt + num_zero;
    }
};
