```
import java.util.ArrayList;
import java.util.List;

import static java.lang.Math.max;


class Solution {
    public int visiblePoints(List<List<Integer>> points, int angle, List<Integer> location) {
        int originCount = 0;
        var angles = new ArrayList<Double>(points.size());
        for (List<Integer> p : points) {
            // translation of p with respect to location:
            var x = p.get(0) - location.get(0);
            var y = p.get(1) - location.get(1);

            if (x == 0 && y == 0) {
                originCount++;
            } else { // if x,y is not origin:
                var degrees = Math.toDegrees(Math.atan2(y, x));
                angles.add(degrees);
                angles.add(degrees + 360); // to avoid modulo arithmetic!
            }
        }
        angles.sort(Double::compareTo);
        var size = angles.size();

        int mx = 0;
        int slow = 0;
        for (int fast = 0; fast < size && slow < size/2;) {
            if (angles.get(fast) - angles.get(slow) <= angle) {
                mx = max(fast - slow + 1, mx);
                if (mx >= size/2) {
                    // all possible points in view
                    break;
                }
                fast++;
            } else {
                slow++;
            }
        }
        return mx + originCount;
    }
}
