class Solution {
    const double RAD_TO_DEGREES = 180.0 / M_PI;

public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        std::vector<double> degrees;
        int result = 0;

        int lx = location.front();
        int ly = location.back();
        for (const vector<int> & point : points) {
            int px = point.front();
            int py = point.back();
            if (lx == px && ly == py) {
                result++;
                continue;
            }

            double degree = std::atan2(py - ly, px - lx) * RAD_TO_DEGREES;
            degrees.push_back(degree);
        }

        std::sort(degrees.begin(), degrees.end());

        size_t size = degrees.size();
        degrees.reserve(2 * size);
        for (int i = 0; i < size; i++) {
            degrees.push_back(degrees[i] + 360);
        }

        int beg = 0;
        int end = 0;
        int max_points = 0;
        while (end < degrees.size()) {
            while (end - beg >= size) {
                beg++;
            }

            double diff = degrees[ end ] - degrees[ beg ];            
            while (diff > angle) {
                diff = degrees[ end ] - degrees[ ++beg ];
            }

            max_points = std::max(max_points, end - beg + 1);
            end++;
        }

        result += max_points;
        return result;
    }
};
