class Solution {
    public int visiblePoints(List<List<Integer>> points, int angle, List<Integer> location) {
        int count = 0;
        List<Double> current = new ArrayList<>();
        for(List<Integer> point: points){

            if(point.get(1) == location.get(1) && point.get(0) == location.get(0)){
                count++;
                continue;
            }
            double ang = Math.atan(((double)point.get(1) - location.get(1))/ ((double)point.get(0) - location.get(0))) * 180 / Math.PI;
            // System.out.println(ang);
            if(ang >= 0 &&   point.get(1)  <= location.get(1) && point.get(0)  <= location.get(0) ){
                ang += 180;
            }
            else if(ang <= 0 && point.get(1) > location.get(1) && point.get(0)  <= location.get(0)  ){
